@import url("https://fonts.googleapis.com/css2?family=Space+Mono:wght@400;700&display=swap");
body {
  font-family: "Space Mono", sans-serif;
  background: hsl(185, 41%, 84%);
  padding: 0;
  margin: 0;
}

* {
  font-weight: 700;
}

button {
  transition: opacity 0.3s ease-in;
}
button:hover {
  opacity: 0.7;
}

ul {
  list-style: none;
  padding: 0;
}

button {
  padding: 0;
  cursor: pointer;
}

.body {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-content: center;
  justify-items: center;
  align-items: center;
  width: 100%;
}

.main-title {
  font-size: 18px;
  line-height: 1.4;
  letter-spacing: 8px;
  color: hsl(186, 14%, 43%);
  margin: 50px auto;
}

.main-container {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 24px;
  box-sizing: border-box;
  min-height: 365px;
  border-radius: 14px;
  padding: 24px;
  overflow: hidden;
  margin: auto;
  background-color: hsl(0, 0%, 100%);
  width: 705px;
}

.parameters-container {
  display: flex;
  flex-direction: column;
  box-sizing: border-box;
  padding: 10px 13px;
}

.label, .discount-container-label {
  font-size: 12px;
  color: hsl(184, 14%, 56%);
  position: relative;
}

.discount-container-label {
  margin: 26px 0 0 0;
}

.input {
  box-sizing: border-box;
  text-align: end;
  width: 100%;
  height: 36px;
  padding: 12px;
  margin-top: 8px;
  font-size: 18px;
  color: hsl(183, 100%, 15%);
  background-color: hsl(189, 41%, 97%);
  border: none;
  border-radius: 8px;
  position: relative;
}
.input:focus {
  outline: none;
  border: 1px solid hsl(172, 67%, 45%);
}

.total::before {
  content: "";
  background-image: url("/57cff7e9185da01dd09bf5c51e22f096.svg");
  width: 11px;
  height: 17px;
  position: absolute;
  left: 12px;
  top: 34px;
  background-repeat: no-repeat;
}

.persons-input::before {
  content: "";
  background-image: url("/e85ea7b9f0cfe85777cbdc8a95411073.svg");
  position: absolute;
  width: 15px;
  height: 17px;
  left: 12px;
  top: 34px;
  background-repeat: no-repeat;
}

.discount-container {
  display: flex;
  flex-direction: row;
  gap: 10px;
  flex-wrap: wrap;
  margin-bottom: 35px;
}

.discount-list-item {
  padding: 0;
}

.button {
  border-radius: 5px;
  border: none;
}
.button.active {
  background-color: hsl(172, 67%, 45%);
}
.button.discount {
  background: hsl(183, 100%, 15%);
  color: hsl(189, 41%, 97%);
  width: 90px;
  height: 37px;
  font-size: 18px;
}
.button.custom-input {
  color: hsl(183, 100%, 15%);
  width: 80px;
  height: 37px;
  background: hsl(189, 41%, 97%);
  text-align: end;
  font-size: 24px;
}
.button.custom-input::placeholder {
  font-size: 16px;
  color: hsl(186, 14%, 43%);
  text-align: center;
  align-content: end;
}
.button.custom-input:focus {
  outline: none;
  border: 1px solid hsl(172, 67%, 45%);
}
.button.reset {
  background-color: hsl(172, 67%, 45%);
  width: 100%;
  height: 35px;
  color: hsl(183, 100%, 15%);
}

.results-container {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  background-color: hsl(183, 100%, 15%);
  box-sizing: border-box;
  padding: 30px;
  border-radius: 14px;
}

.list {
  display: flex;
  flex-direction: column;
  gap: 24px;
}

.title-container {
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.result {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
}
.result .title {
  font-size: 14px;
  color: hsl(189, 41%, 97%);
  margin: 0;
}
.result .span {
  font-size: 10px;
  color: hsl(184, 14%, 56%);
}
.result .sum {
  font-size: 36px;
  color: hsl(172, 67%, 45%);
  margin: 0;
}

@media screen and (max-width: 750px) {
