@mixin flex-column {
  display: flex;
  flex-direction: column;
}

@mixin flex-row {
  display: flex;
  flex-direction: row;
}

@mixin flex-center {
  justify-content: center;
  align-content: center;
  justify-items: center;
  align-items: center;
}
